mapper.select:
SELECT LOCATION_ID as locationId,
       SIDO as sido, 
       SIGUNGU as sigungu
  FROM LOCATION
 WHERE SIDO    = #{sido}
   AND SIGUNGU = #{sigungu}

mapper.insert:

  SELECT SEQ_LOCATION_ID.NEXTVAL FROM DUAL


INSERT INTO LOCATION (
  LOCATION_ID,
  SIDO,
  SIGUNGU
) VALUES (
  #{locationId},
  #{sido},
  #{sigungu}
)
